INSERT INTO `cont_2contenidos` VALUES (46,27,2,5,'<h3>Doña Jovita a Mamá Antula</h3><p>Doña Jovita -interpretada por José Luis Serrano- es una abuela del interior de la Argentina, humilde, que exhuda la sabiduría de los años y las adversidades de la vida. En esta genial payada describe aspectos de la vida y obra de Mama Antula, predecesora del Cura Brochero en la difusión de los Ejercicios Espirituales.</p>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-11-29 20:28:41','2025-12-02 23:47:54',1,3);
