INSERT INTO `cont_2contenidos` VALUES (249,88,2,2,'<blockquote>Este es un fragmento de aquella carta. Ante la muerte de Mama Antula, Juárez le pide a Funes que le envíe toda la información biográfica de La Beata para escribir una «Relación» (es decir, un acta). Menciona a su sucesora, Margarita Melgarejo.</blockquote>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-03 12:51:55','2025-12-03 12:54:11',1,3);
INSERT INTO `cont_2contenidos` VALUES (250,89,2,2,'<p>Roma, 10 de marzo de 1801.</p><p>… sus últimas que he recibido son del (17)99: la una del 16 de febrero y la otra del 16 de abril. A esta última acompañaban con la noticia de la preciosa muerte de nuestra <strong>Beata María Antonia</strong>, su testamento y una parte de la Relación hecha en Buenos Aires de algunas cosas concomitantes y otras subsiguientes a dicho fallecimiento, con la oferta del resto de dicha Relación, si es que se hubiese continuado.</p>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-03 12:53:51','2025-12-03 12:54:12',1,3);
