INSERT INTO `cont_2contenidos` VALUES (346,106,2,1,'<blockquote>Del Presbítero Pedro Francisco de Uriarte, Capellán de la Casa de Ejercicios, a un sacerdote en Roma. El Padre Uriarte (primo del Padre Gaspar Juárez) se maravilla por la abundancia de alimentos de la casa de Ejercicios Espirituales, que la gente ha dado en llamar «La casa de la Providencia» y la que ha llegado a reunir a 500 personas durante 10 días, por lo que los ejercitantes han tenido que dormir en el suelo por falta de camas. Comenta el milagro de la multiplicación de los panes y se alegra por «la erradicación de todos los vicios y pecados» de los que los realizan y de que los jóvenes del virreinato «abracen con gusto la pobreza evangélica».</blockquote>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-05 01:40:43','2025-12-05 01:47:53',1,3);
