INSERT INTO `cont_2contenidos` VALUES (392,120,2,1,'<blockquote>Se desconoce la fecha precisa en que fue escrita esta carta, aunque se cree que probablemente en el año 1787. Se indica que esta carta llegó a Roma a fines de ese año. Funes relata los mismos sucesos ya relatados en la <a href=\"/cartas-y-escritos/cartas?id=109\">carta 42</a> respecto a Mama Antula y una de sus beatas mulatas, Catalina.</blockquote>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-05 17:40:57','2025-12-05 17:57:58',1,3);
INSERT INTO `cont_2contenidos` VALUES (393,121,2,1,'<blockquote>Juárez le responde a Funes su <a href=\"/cartas-y-escritos/cartas?id=119\">carta anterior</a> para que se quede tranquilo respecto a la difusión de sus cartas en Europa, en las cuales describe vida y obra de Mama Antula.</blockquote>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-05 17:48:40','2025-12-05 17:58:02',1,3);
