INSERT INTO `cont_2contenidos` VALUES (499,150,2,2,'<p>Roma, 10 de julio de 1793</p><p>… La <strong>Beata</strong> me escribe que no nos quiere sino con las sotanas (que usaban los Jesuitas) y en nuestros colegios y ministerios; y quizás ella no quiere sino sólo lo que Dios quiere…</p>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-07 10:13:25','2025-12-07 10:22:33',1,3);
