INSERT INTO `cont_2contenidos` VALUES (504,152,2,1,'<blockquote>La fecha de esta carta coincide con la fecha de la carta anterior (85). En ésta Mama Antula vislumbra que a los Jesuitas no les ha de faltar nada, y pide que no dejen de escribirle aunque no responda.</blockquote>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-07 10:16:30','2025-12-07 10:22:34',1,3);
