INSERT INTO `cont_2contenidos` VALUES (518,155,1,1,'<p>Es un Monumento Histórico Nacional.</p><p>En la Santa Casa, Mama Antula congregaba al pueblo porteño colonial en numerosas tandas de Ejercicios Espirituales. Aquí murió nuestra santa argentina un 7 de marzo de 1799.</p><p>Construida por María Antonia de Paz y Figueroa, la Santa Casa de Ejercicios Espirituales es, además de un lugar santo, un museo que atesora reliquias religiosas e históricas de la Argentina.</p>','1765107129779.jpg',NULL,NULL,'La Santa Casa de Ejercicios Espirituales es uno de los edificios más antiguos de Buenos Aires (1794), queda en Av. Independencia 1190, CABA\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-07 11:32:09','2025-12-08 11:55:50',1,3);
