INSERT INTO `cont_2contenidos` VALUES (542,163,2,1,'<blockquote>Esto es un fragmento de aquella carta. Letamendi -al igual que en las cartas 95 y 96- hace referencia a la famosa Oración Fúnebre que pronunció el Padre Perdriel en los funerales de Mama Antula, que no puede conseguir todavía.</blockquote>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-08 11:51:35','2025-12-08 11:55:43',1,3);
INSERT INTO `cont_2contenidos` VALUES (543,163,1,2,'<p>… No menos temo que el Padre Perdriel nos deje sin la Oración Fúnebre de la Madre Beata. Sé que la ha negado distintas veces y, aunque me ofreció francamente remitiría a Vm. en sacándola en limpio, lo más factible es que no lo hará, si no se la cobra Vm. por medio de cuatro letras.</p>','1765194775151.jpg',NULL,NULL,'Mayólica de la Celda 9 de la Santa Casa de Ejercicios Espirituales de Mama Antula.\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-08 11:52:55','2025-12-08 11:55:43',1,3);
