INSERT INTO `cont_2contenidos` VALUES (555,167,2,2,'<p>Las últimas que después de una interrupción de tiempo, desde que se enfurecieron las guerras, recibí de Vm. fueron dos juntas con fecha 16 de abril y de 16 de febrero, ambas de 1799. A éstas acompañan un capítulo de carta de 17 de marzo del mismo ‘99 sobre la muerte de nuestra Beata y una copia de su testamento. Así éstas, como sus citadas, fueron para mí de gran consuelo y de mi mayor estimación. A las suyas respondí desde luego el año pasado agradeciendo su atención y cuidado.</p>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-08 12:01:42','2025-12-08 12:18:52',1,3);
