INSERT INTO `cont_2contenidos` VALUES (149,69,2,2,'<blockquote>Mama Antula le escribe a Ambrosio felicitándolo por su hija, se apena de los funestos sucesos del Virreinato del Perú (la ejecución del indio Tupác Amarú II luego de la protagonizar la mayor rebelión indígenea anticolonial del siglo XVIII) y comenta que a la fecha hace 29 semanas que no para de ofrecer los Ejercicios Espirituales con éxito.</blockquote>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-11-23 01:11:44','2025-12-02 23:47:54',1,3);
