INSERT INTO `cont_2contenidos` VALUES (416,126,1,2,'<p>… Haces muy bien en comunicarnos con Don Alberto mis cartas, lo mismo hago yo aquí con <em>Don Gaspar</em> y los demás de mis compañeros con las vuestras; de esa manera evitan repeticiones; pero sobre al Apostolado de la <strong>Señora Beata</strong> quiero que en particular me digas lo que supieres fundado, pues aquí son celebradas con razón sus fatigas.</p>','1764966584702.jpg',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-05 20:29:44','2025-12-05 20:36:52',1,3);
