INSERT INTO `cont_2contenidos` VALUES (461,138,2,2,'<p>… Lo de <em>Catalina</em> y lo de la <em>Beata</em>, al mismo tiempo todo esto juntamente con la viva fe de Vd. que la considero como la del Santo Abraham, no hay duda que todo esto es una gran colección de fundamentos muy sólidos, para formar un juicio prudente de que se acerca sin duda aquel momento feliz de la restauración del gran templo de la gloria de Dios.</p>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-06 15:08:46','2025-12-06 17:27:56',1,3);
