INSERT INTO `cont_2contenidos` VALUES (511,154,2,2,'<p>Al hablar de su deliberación de ir de misionero a Norte América y así evadirse de la invasión francesa y su devastación en Italia, dice: … pido a Vd. como lo hago también con la <strong>Beata</strong>, que encomienden al Señor este mi negocio para que, por su amabilísimo Corazón, me alumbre y aun facilite su ejecución.</p>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-07 10:21:55','2025-12-07 10:22:26',1,3);
