INSERT INTO `cont_2contenidos` VALUES (540,162,2,2,'<p>Buenos Aires, 26 de agosto de 1799</p><p>… Me ha dicho el Padre Perdriel que luego que saque en limpio la Oración Fúnebre de la Madre Beata se la mandará a Vm. Veremos si lo cumple.</p>',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2025-12-08 11:50:09','2025-12-08 11:55:42',1,3);
