INSERT INTO `cont_2contenidos` VALUES (22,15,3,4,NULL,'1762640635083.jpg',NULL,NULL,'Retrato de Mama Antula pintado por José de Salas, «el madrileño», que conoció a la beata. El retrato fue colocado en la Misa exequial en la que el Padre Perdriel pronunció la Oración Fúnebre. Cuando murió Mama Antula el 7 de marzo de 1779 se cumplió su voluntad: fue enterrada muy sencillamente, sin ataúd ni funerales, en el camposanto de la antigua Iglesia de La Piedad.',NULL,NULL,NULL,NULL,1,'2025-11-08 22:23:55',1,3);
